INSERT INTO afm_flds_lang (table_name, field_name) SELECT table_name, field_name FROM afm_flds WHERE NOT EXISTS (SELECT 1 FROM afm_flds_lang afm_flds_lang_inner WHERE afm_flds_lang_inner.table_name = afm_flds.table_name AND afm_flds_lang_inner.field_name = afm_flds.field_name) AND afm_flds.table_name IN ('wohwo','wrhwr','wrview');

BEGIN EXECUTE IMMEDIATE 'ALTER TABLE wr ADD satisfaction NUMBER(*,0) DEFAULT 0 NULL'; EXCEPTION WHEN OTHERS THEN null;END;;
BEGIN EXECUTE IMMEDIATE 'ALTER TABLE wr ADD satisfaction_notes VARCHAR2(500) DEFAULT NULL NULL'; EXCEPTION WHEN OTHERS THEN null;END;;
BEGIN EXECUTE IMMEDIATE 'ALTER TABLE hwr ADD satisfaction NUMBER(*,0) DEFAULT 0 NULL'; EXCEPTION WHEN OTHERS THEN null;END;;
BEGIN EXECUTE IMMEDIATE 'ALTER TABLE hwr ADD satisfaction_notes VARCHAR2(500) DEFAULT NULL NULL'; EXCEPTION WHEN OTHERS THEN null;END;;
BEGIN EXECUTE IMMEDIATE 'ALTER TABLE afm_groups ADD ww_preferences VARCHAR2(2000) DEFAULT NULL NULL'; EXCEPTION WHEN OTHERS THEN null;END;;

